define("common/js/nooline/validate", [],function(){
  ;(function buildValidate (N) {
    
    N.validate = function validate (strings) {
      
      var rules = {
        username: function (string) {
          if ((string.length > 2) 
            && (/^[\w\d\s]+$/.test(string))) {
            return true;
          }
        },
        
        password: function (string) {
          if ((string.length > 12)
            && (/^[\w\d\s!@#$%^&*?<>]+$/.test(string))) {
            return true;
          }
        }
      };
      
      var valid = _.every(strings, function checkRules (string, test) {
        
        if (rules[test]) {
          
          return rules[test](string);
          
        } else {
          
          return false;
          
        }
      });
      
      return valid;
      
    };
    
  }(window.Nooline))
});

define("common/js/nooline/reject-login", [],function(){
  ;(function buildRejectLogin (N) {
    
    N.rejectLogin = function rejectLogin ($section) {
      var message = 'Looks like those credentials are invalid.  '
        + 'Make sure that the '
        + '<em>username</em> is at least <em>2</em> characters long, '
        + 'containing only <em>letters</em>, <em>numbers</em>, and '
        + '<em>spaces</em>, and that '
        + 'the <em>password</em> is at least <em>12</em> '
        + 'characters long, containing <em>letters</em>, <em>numbers</em>, '
        + '<em>spaces</em>, and any of '
        + 'the following symbols: <em>!@#$%^&*<>?</em>';

      var $error = $('<p class="error-message hidden"></p>')
        .html(message);
      
      $section
        .addClass('invalid')
        .append($error);
      
      console.log('Credentials rejected.');
      
      setTimeout(function showError () {
        $error.removeClass('hidden');
        
        delete N.$activeLogin;
      }, 0);
    };
    
  }(window.Nooline));
});

define("common/js/nooline/hash-this", [],function(){
  ;(function buildHashThis (N) {
    
    N.hashThis = function hashThis (string) {
      
      var hash = Crypto.SHA3(string);
      
      var encoded = hash.toString(Crypto.enc.Base64);
      
      return encoded;
      
    };
    
  }(window.Nooline));
});

define("common/js/nooline/post-login", [],function(){
  ;(function buildPostLogin (N) {
    
    N.postLogin = function receiveLogin (username, password, type) {
      
      var lastLoginAttempt = {
        'username': username,
        'password': password,
        timestamp: Date.now()
      };
      
      sessionStorage.setItem(
        'lastLoginAttempt', 
        JSON.stringify(lastLoginAttempt)
      );
      
      console.log('Posting credentials...');
      
      $.post('/login', {
        'username': username,
        'password': password,
        'type': type
      }, N.receiveLogin);
      
    };
    
  }(window.Nooline));
});
