var crc32=null,test=null.test;test("simple crc32 is no problem",function(e){var n=Buffer("hey sup bros"),c=Buffer([71,250,85,112]);e.same(crc32(n),c),e.end()}),test("another simple one",function(e){var n=Buffer("IEND"),c=Buffer([174,66,96,130]);e.same(crc32(n),c),e.end()}),test("slightly more complex",function(e){var n=Buffer([0,0,0]),c=Buffer([255,65,217,18]);e.same(crc32(n),c),e.end()}),test("complex crc32 gets calculated like a champ",function(e){var n=Buffer("शीर्षक"),c=Buffer([23,184,175,241]);e.same(crc32(n),c),e.end()}),test("casts to buffer if necessary",function(e){var n="शीर्षक",c=Buffer([23,184,175,241]);e.same(crc32(n),c),e.end()}),test("can do signed",function(e){var n="ham sandwich",c=-1891873021;e.same(crc32.signed(n),c),e.end()}),test("can do unsigned",function(e){var n="bear sandwich",c=3711466352;e.same(crc32.unsigned(n),c),e.end()}),test("simple crc32 in append mode",function(e){var n,c,r=[Buffer("hey"),Buffer(" "),Buffer("sup"),Buffer(" "),Buffer("bros")],s=Buffer([71,250,85,112]);for(n=0,c=0;c<r.length;c++)n=crc32(r[c],n);e.same(n,s),e.end()}),test("can do signed in append mode",function(e){var n="ham",c=" ",r="sandwich",s=-1891873021,f=crc32.signed(n);f=crc32.signed(c,f),f=crc32.signed(r,f),e.same(f,s),e.end()}),test("can do unsigned in append mode",function(e){var n="bear san",c="dwich",r=3711466352,s=crc32.unsigned(n);s=crc32.unsigned(c,s),e.same(s,r),e.end()});