function convert(r){var t=1<<30,n=1<<20,b=1024;return r>=t?Math.round(100*(r/t))/100+"gb":r>=n?Math.round(100*(r/n))/100+"mb":r>=b?Math.round(100*(r/b))/100+"kb":r+"b"}module.exports=function(r){var t,n,b,o;return"number"==typeof r?convert(r):(t=r.match(/^(\d+(?:\.\d+)?) *(kb|mb|gb)$/),n=parseFloat(t[1]),b=t[2],o={kb:1024,mb:1<<20,gb:1<<30},o[b]*n)};