var crypto=null;exports.sign=function(r,e){if("string"!=typeof r)throw new TypeError("cookie required");if("string"!=typeof e)throw new TypeError("secret required");return r+"."+crypto.createHmac("sha256",e).update(r).digest("base64").replace(/\=+$/,"")},exports.unsign=function(r,e){if("string"!=typeof r)throw new TypeError("cookie required");if("string"!=typeof e)throw new TypeError("secret required");var t=r.slice(0,r.lastIndexOf("."));return exports.sign(t,e)==r?t:!1};