var assert=null,ref=null;describe("bool",function(){var t=ref.alloc("bool");it('should return JS "false" for a value of 0',function(){t[0]=0,assert.strictEqual(!1,ref.get(t))}),it('should return JS "true" for a value of 1',function(){t[0]=1,assert.strictEqual(!0,ref.get(t))}),it('should write a JS "false" value as 0',function(){ref.set(t,0,!1),assert.strictEqual(0,t[0])}),it('should write a JS "true" value as 1',function(){ref.set(t,0,!0),assert.strictEqual(1,t[0])}),it("should allow uint8 number values to be written to it",function(){var e=255;ref.set(t,0,e),assert.strictEqual(!0,ref.get(t)),assert.strictEqual(e,t[0])})});