var assert=null,ref=null,ffi=null;describe("ffi_cif",function(){afterEach(gc),it("should return a Buffer representing the `ffi_cif` instance",function(){var e=ffi.CIF(ref.types.void,[]);assert(Buffer.isBuffer(e))}),it('should throw an Error when given an invalid "type"',function(){var e,n=new ffi.FFI_TYPE;n.size=0,n.alignment=0,n.type=0,n.elements=ref.NULL,e={size:1,indirection:1,ffi_type:n.ref()},assert.throws(function(){ffi.CIF(e,[])},function(e){return assert(-1!==e.message.indexOf("FFI_BAD_TYPEDEF")),assert.equal("FFI_BAD_TYPEDEF",e.code),assert.equal(ffi.FFI_BAD_TYPEDEF,e.errno),!0})}),it("should throw an Error when given an invalid ABI argument",function(){assert.throws(function(){ffi.CIF(ref.types.void,[],-1)},function(e){return assert(-1!==e.message.indexOf("FFI_BAD_ABI")),assert.equal("FFI_BAD_ABI",e.code),assert.equal(ffi.FFI_BAD_ABI,e.errno),!0})})});