var assert=null,ref=null,ffi=null,DynamicLibrary=ffi.DynamicLibrary;describe("DynamicLibrary",function(){it("should be a function",function(){assert.equal("function",typeof DynamicLibrary)}),it('should return a "DynamicLibrary" instance when invoked',function(){var r="win32"==process.platform?"msvcrt":"libc",i=DynamicLibrary(r+ffi.LIB_EXT);assert(i instanceof DynamicLibrary)}),describe("get()",function(){it('should return a "pointer" Buffer to a symbol',function(){var r="win32"==process.platform?"msvcrt":"libc",i=DynamicLibrary(r+ffi.LIB_EXT),e=i.get("free");assert(Buffer.isBuffer(e)),assert.equal(0,e.length)}),it('should set the "name" property to the name of the symbol',function(){var r="win32"==process.platform?"msvcrt":"libc",i=DynamicLibrary(r+ffi.LIB_EXT),e="free",n=i.get(e);assert.equal(e,n.name)})})});