var assert=null,ref=null,ffi=null;describe("types",function(){describe("`ffi_type` to ref type matchups",function(){Object.keys(ref.types).forEach(function(f){it('should match a valid `ffi_type` for "'+f+'"',function(){var e=ref.types[f],t=ffi.ffiType(e);assert(Buffer.isBuffer(t))})}),it('should match a valid `ffi_type` for "ref" type without a cached value',function(){var f,e=Object.create(ref.types.void);e.ffi_type=void 0,f=ffi.ffiType(e),assert(Buffer.isBuffer(f))}),it("should match a valid `ffi_type` for `CString` without a cached value",function(){var f,e=Object.create(ref.types.CString);e.ffi_type=void 0,f=ffi.ffiType(e),assert(Buffer.isBuffer(f))}),it("should match a valid `ffi_type` for `ulong` without a cached value",function(){var f,e=Object.create(ref.types.ulong);e.ffi_type=void 0,f=ffi.ffiType(e),assert(Buffer.isBuffer(f))})})});