var Minimatch=require("../minimatch.js").Minimatch,tap=require("tap");tap.test("cache test",function(a){var e,t=new Minimatch("a?b"),i=new Minimatch("a?b");for(a.equal(t,i,"should get the same object"),e=0;100>e;e++)new Minimatch("a"+e);i=new Minimatch("a?b"),a.notEqual(t,i,"cache should have dropped"),a.end()});