var mod_fs = null;
var mod_ctype = null;
var mod_assert = null;

function test()
{
	var data, parser;

	data = JSON.parse(mod_fs.readFileSync('./typedef.json').toString());
	parser = mod_ctype.parseCTF(data, { endian: 'big' });
	mod_assert.deepEqual(parser.lstypes(), { 'bar_t': 'int',
	    'int': 'int32_t' });
}

test();
