var moment=require("../../moment");exports.days_in_month={"days in month":function(e){e.expect(24);var n,o=[31,29,31,30,31,30,31,31,30,31,30,31];for(n=0;12>n;n++)e.equal(moment([2012,n]).daysInMonth(),o[n],moment([2012,n]).format("L")+" should have "+o[n]+" days. (beginning of month "+n+")");for(n=0;12>n;n++)e.equal(moment([2012,n,o[n]]).daysInMonth(),o[n],moment([2012,n,o[n]]).format("L")+" should have "+o[n]+" days. (end of month "+n+")");e.done()},"days in month leap years":function(e){e.expect(4),e.equal(moment([2010,1]).daysInMonth(),28,"Feb 2010 should have 28 days"),e.equal(moment([2100,1]).daysInMonth(),28,"Feb 2100 should have 28 days"),e.equal(moment([2008,1]).daysInMonth(),29,"Feb 2008 should have 29 days"),e.equal(moment([2e3,1]).daysInMonth(),29,"Feb 2000 should have 29 days"),e.done()}};