var moment=require("../../moment");exports.utc={setUp:function(e){moment.lang("en"),e()},tearDown:function(e){moment.lang("en"),e()},"utc and local":function(e){e.expect(7);var t,o,u=moment(Date.UTC(2011,1,2,3,4,5,6));u.utc(),e.equal(u.date(),2,"the day should be correct for utc"),e.equal(u.day(),3,"the date should be correct for utc"),e.equal(u.hours(),3,"the hours should be correct for utc"),u.local(),u.zone()>180?(e.equal(u.date(),1,"the date should be correct for local"),e.equal(u.day(),2,"the day should be correct for local")):(e.equal(u.date(),2,"the date should be correct for local"),e.equal(u.day(),3,"the day should be correct for local")),t=Math.ceil(u.zone()/60),o=(27-t)%24,e.equal(u.hours(),o,"the hours ("+u.hours()+") should be correct for local"),e.equal(moment().utc().zone(),0,"timezone in utc should always be zero"),e.done()},"creating with utc":function(e){e.expect(7);var t,o=moment.utc().valueOf()-moment().valueOf();o=Math.abs(o),e.ok(5>o,"Calling moment.utc() should default to the current time"),t=moment.utc([2011,1,2,3,4,5,6]),e.equal(t.date(),2,"the day should be correct for utc array"),e.equal(t.hours(),3,"the hours should be correct for utc array"),t=moment.utc("2011-02-02 3:04:05","YYYY-MM-DD HH:mm:ss"),e.equal(t.date(),2,"the day should be correct for utc parsing format"),e.equal(t.hours(),3,"the hours should be correct for utc parsing format"),t=moment.utc("2011-02-02T03:04:05+00:00"),e.equal(t.date(),2,"the day should be correct for utc parsing iso"),e.equal(t.hours(),3,"the hours should be correct for utc parsing iso"),e.done()},"creating with utc without timezone":function(e){e.expect(4);var t=moment.utc("2012-01-02T08:20:00");e.equal(t.date(),2,"the day should be correct for utc parse without timezone"),e.equal(t.hours(),8,"the hours should be correct for utc parse without timezone"),t=moment.utc("2012-01-02T08:20:00+09:00"),e.equal(t.date(),1,"the day should be correct for utc parse with timezone"),e.equal(t.hours(),23,"the hours should be correct for utc parse with timezone"),e.done()},"cloning with utc":function(e){e.expect(4);var t=moment.utc("2012-01-02T08:20:00");e.equal(moment.utc(t)._isUTC,!0,"the local zone should be converted to UTC"),e.equal(moment.utc(t.clone().utc())._isUTC,!0,"the local zone should stay in UTC"),t.zone(120),e.equal(moment.utc(t)._isUTC,!0,"the explicit zone should stay in UTC"),e.equal(moment.utc(t).zone(),0,"the explicit zone should have an offset of 0"),e.done()},"weekday with utc":function(e){e.expect(1),e.equal(moment("2013-09-15T00:00:00Z").utc().weekday(),moment("2013-09-15T23:59:00Z").utc().weekday(),"a UTC-moment's .weekday() should not be affected by the local timezone"),e.done()}};