var Sender=null;describe("Sender",function(){describe("#frameAndSend",function(){it("does not modify a masked binary buffer",function(){var e=new Sender({write:function(){}}),n=new Buffer([1,2,3,4,5]);e.frameAndSend(2,n,!0,!0),n[0].should.eql(1),n[1].should.eql(2),n[2].should.eql(3),n[3].should.eql(4),n[4].should.eql(5)}),it("does not modify a masked text buffer",function(){var e=new Sender({write:function(){}}),n="hi there";e.frameAndSend(1,n,!0,!0),n.should.eql("hi there")})})});